import React, {useState, useEffect} from "react"
import { useHistory } from "react-router"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrashAlt } from "@fortawesome/free-regular-svg-icons"
import { faArrowsAlt} from "@fortawesome/free-solid-svg-icons"
import WithoutVariant from "./AddProductComponent/WithoutVariant"

const AddProduct = (props) =>{
    const history = useHistory();
    const MAX_VARIANT = 20;
    const [dragidx,setdragIdx] = useState(0);
    const [onDragWait, setOndragWait] = useState(false);
    const [withVariant,setWithVariant] = useState(false);
    const [variantList,setVariantList] = useState([{ text:"", style:{transform:"translateY(0%)"}, price:0, stok:0 }]);
    const [productName,setProductName] = useState("");
    const [price,setPrice] = useState("");
    const [stok,setStok] = useState("");


    const handleVariantChange = () =>{
        setWithVariant(!withVariant)
    }

    const addVariantList = () =>{
        if(variantList.length <= MAX_VARIANT){
            let newVariantList = [...variantList];
            newVariantList.push({text:"", style:{transform:"translateY(0%)"}, price:0, stok:0 });
            setVariantList(newVariantList);
        }

    }

    const handleInputChange = idx => (e) =>{
        let newVariant = [...variantList];
        newVariant[idx].text = e.target.value;
        setVariantList(newVariant)
    }

    const deleteVariant = idx => (e) =>{
        let newVariantList = [...variantList];
        newVariantList.splice(idx,1);
        setVariantList(newVariantList)
    }

    const handleChangeOne = action => (e) =>{
        switch(action){
            case 0:
                setProductName(e.target.value);
                break;
            default:
                break;
        }
    }

    const handleChangeVariantList = (i,act) => (e) =>{
        let newVariantList = [...variantList];
        switch(act){
            case 0:
                newVariantList[i].price = e.target.value;
                break;
            case 1:
                newVariantList[i].stok = e.target.value;
                break;
            default:
                break;
        }

        setVariantList(newVariantList);
    }

    const goBack = () =>{
        history.push("/main/inventory")
    }

    return(
        <div className="addProductPage">
           <h3 className="title">Informasi Produk</h3>
           <div className="input-group">
                <label>Nama Produk</label>
                <input type="text" name="productName" value={productName} onChange={handleChangeOne(0)} placeholder="input"/>
           </div>

            {
            !withVariant ? 
                <WithoutVariant setPrice={setPrice} setStok={setStok} price={price} stok={stok} handleVariantChange={handleVariantChange} />
            : 
                <div>
                    <div className="variant-container">
                        <label>Variasi 1</label>
                        <div className="variant-box">
                            <span className="close" onClick={handleVariantChange}>x</span>
                            <div className="input-group">
                                <label>Nama</label>
                                <input type="text" name="variantName" placeholder="Masukan nama variasi, contoh: warna,dll"/>
                            </div>
                            {variantList.map((x,i)=>{
                                return(
                                    <div className="input-group" dragbox="1" style={x.style} key={i} draggable={true} onDragStart={dragStart(i)} onDragEnter={dragEnter(i)} onDragOver={dragOver(i)}>
                                        { (i === 0) ? <label>Pilihan</label> : <label></label>}
                                        <input type="text" name="variantChoice" value={x.text} onChange={handleInputChange(i)} placeholder="Masukan pilihan variasi, contoh: Merah,dll"/>
                                        {(variantList.length > 1) ? 
                                            <div className="icon-wrapper">
                                                <FontAwesomeIcon icon={faTrashAlt} className="input-icon" onClick={deleteVariant(i)}/>
                                                <FontAwesomeIcon icon={faArrowsAlt} className="input-icon" id="draggableIcon"  draggable={true}/>                                       
                                            </div>
                                        : ""}
                                    </div>   
                                )                       
                            })}
                            <div className="input-group">
                                <label></label>
                                <button type="button" className="addVariasi" onClick={addVariantList}>+ Tambahkan Pilihan ({variantList.length + "/" + MAX_VARIANT})</button>
                            </div>  
                        </div>
                    </div> 

                    <div className="table-container">
                        <label>Daftar Variasi</label>
                        <table>
                            <thead>
                                <tr>
                                    <td className="thead">Nama</td>
                                    <td className="thead">Harga</td>
                                    <td className="thead">Stok</td>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    variantList.map((x,i)=>{
                                        return(
                                            <tr key={i}>
                                                <td>{x.text}</td>
                                                <td>
                                                    <input type="number" min="0" value={x.price} onChange={handleChangeVariantList(i,0)}/>
                                                </td>
                                                <td>
                                                    <input type="number" min="0" value={x.stok} onChange={handleChangeVariantList(i,1)}/>
                                                </td>
                                            </tr>
                                        )
                                    })
                                }
                            </tbody>

                        </table>
                    </div>                   
                </div>       
            }

            <div className="row_add_product">
                <button className="backButton" onClick={goBack}>Kembali</button>
                <button className="insertButton">Simpan</button>
            </div>
        
        </div>
    )   
}

export default AddProduct;
