import React, {useState, useEffect} from "react"
import "../../css/inventory.css"
import ProductsDataService from '../../services/products'
import ProductItems from './InventoryComponents/ProductItem'
import { useHistory } from "react-router"
import AddProductModal from "./InventoryComponents/AddProductModal";

const Inventory = (props) =>{
    const history = useHistory();
    const [query,setQuery] = useState("");

    const [searchInput,setSearchInput] = useState("");
    const [selectedSearch,setSelectedSearch] = useState("0");
    const [ownedProducts,setOwnedProducts] = useState([]);
    const [overlayStyle,setOverlayStyle] = useState({});

    useEffect(()=>{
        refreshProductsList();
    },[])

    const refreshProductsList = () =>{
        ProductsDataService.getAllOwnedProducts().then((res)=>{
            setOwnedProducts(res.data);
        });
    }

    const typeQueryBox = (e) =>{
        setQuery(e.target.value)
    }    

    const addProductToggle = () =>{
        setOverlayStyle({left:0});
    }

    const closeModal = () =>{
        setOverlayStyle({left:"100%"})
    }

    const handleChange = (e) =>{
        switch(e.target.id){
            case "queryType":
                setSelectedSearch(e.target.value);
                break;
            case "searchInput":
                setSearchInput(e.target.value);
                break;
            default:
                break;
        }
        
    }

    return(
        <div id="main-inventory">
            <div className="queryBoxContainer">
                <div className="inventory-row" style={{marginBottom:"2rem"}}>
                    <select name="searchBox" className="queryType" id="queryType" value={selectedSearch} onChange={handleChange}>
                        <option value="0">Nama Produk</option>
                        <option value="1">Variasi</option>
                    </select>
                    <input type="text" id="searchInput" name="searchInput" value={searchInput} className="searchInput" placeholder="Input"/>
                </div>
                <div className="inventory-row" style={{marginBottom:"2rem"}}>
                    <div className="rangeInputContainer" style={{paddingRight:"1rem"}}>
                        <label>Stok</label>
                        <input type="number" id="stokDown" name="stokDown" className="rangeInput" placeholder="input" />
                        <hr></hr>
                        <input type="number" id="stokUp" name="stokUp" className="rangeInput" placeholder="input" />
                    </div>
                    <div className="rangeInputContainer" style={{paddingLeft:"1rem"}}>
                        <label>Penjualan</label>
                        <input type="number" id="sellDown" name="sellDown" className="rangeInput" placeholder="input" />
                        <hr></hr>
                        <input type="number" id="sellUp" name="sellUp" className="rangeInput" placeholder="input" />
                    </div>
                </div>
                <div className="inventory-row">
                    <button className="searchButton">Cari</button>
                    <button className="resetButton">Atur Ulang</button>
                </div>
            </div>
        </div>
    )
}

export default Inventory;

