import React, {useState, useEffect} from "react"
import "../../css/inventory.css"
import ProductsDataService from '../../services/products'
import {
    Switch,
    Route,
    useRouteMatch
  } from "react-router-dom";
import { useHistory } from "react-router"
import MainInventory from "./InventoryComponents/MainInventory"


const Inventory = (props) =>{
    const history = useHistory();
    const [query,setQuery] = useState("");

    const { path, url } = useRouteMatch();
    const [addProdPanelSlide,setAddProdPanelSlide] = useState({transform:"translateX(100%)"});
    const [ownedProducts,setOwnedProducts] = useState([]);
    const [overlayStyle,setOverlayStyle] = useState({});

    useEffect(()=>{
        refreshProductsList();
    },[])

    const refreshProductsList = () =>{
        ProductsDataService.getAllOwnedProducts().then((res)=>{
            setOwnedProducts(res.data);
        });
    }

    const typeQueryBox = (e) =>{
        setQuery(e.target.value)
    }    

    return(
        <div id="main-inventory-container">
            <Switch>
                <Route exact path={[path,`${path}/inventory`]} >
                    <MainInventory/>
                </Route>
                <Route exact path={[`${path}/addProduct`]} >
                    <Order />
                </Route>
            </Switch>
        </div>
    )
}

export default Inventory;

