import React, {useState, useEffect} from "react"
import "../../css/inventory.css"
import sampleImage from '../../images/products/Excalibur.png'
import tokopedia from '../../images/website/tokopedia-48.png'
import shopee from '../../images/website/shopee-48.png'
import lazada from '../../images/website/lazada-48.png'
import ProductsDataService from '../../services/products'

const Inventory = (props) =>{
    const [query,setQuery] = useState("");
    const [ownedProducts,setOwnedProducts] = useState([]);

    useEffect(()=>{
        getOwnedProducts();
    },[])

    const getOwnedProducts = () =>{
        ProductsDataService.getAllOwnedProducts().then((res)=>{
            setOwnedProducts(res.data);
        });
    }

    const typeQueryBox = (e) =>{
        setQuery(e.target.value)
    }    

    return(
        <div id="main-inventory">
            <div className="queryBoxContainer">
                <input type="text" name="queryBox" id="queryBox" className="acc-text-input" value={query} onChange={typeQueryBox} placeholder="Search product here" /> 
                <button className="add-product">Add Product</button>
            </div>
            <div className="productListContainer">
                {ownedProducts.map(x=>{
                    return(
                        <div className="productItem">
                            <img src={`../images/products/Excalibur.png`} className="productItemImg" /> 
                            <div className="rightImage">
                                <h4 className="productTitle">{x.name}</h4>
                                <div className="secondRow">
                                    <div className="stockLabel"><span className="stockLabelText">Stock</span></div>
                                    <div className="stock" style={{marginRight:"2rem"}}><span className="stockText">{x.price}</span></div>
                                    <div className="stock"><span className="stockText">8 variant</span></div>
                                </div>
                                <div className="thirdRow">
                                    <div className="icon-container">
                                        <img src={tokopedia} className="thirdRowIcons" />
                                        <img src={shopee} className="thirdRowIcons" />
                                        <img src={lazada} className="thirdRowIcons" />
                                    </div>
                                    <button className="editProductButton">Edit</button>
                                </div>
                            </div>
                        </div>
                    )
                })}

            </div>
        </div>
    )
}

export default Inventory;
