import React, {useState, useEffect} from "react"
import { useHistory } from "react-router"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrashAlt } from "@fortawesome/free-regular-svg-icons"
import { faArrowsAlt} from "@fortawesome/free-solid-svg-icons"
import WithoutVariant from "./AddProductComponent/WithoutVariant"
import WithVariant from "./AddProductComponent/WithVariant"


const AddProduct = (props) =>{
    const history = useHistory();
    
    const [withVariant,setWithVariant] = useState(false);
    const [variantList,setVariantList] = useState([{ text:"", style:{transform:"translateY(0%)"}, price:0, stok:0 }]);
    const [productName,setProductName] = useState("");
    const [price,setPrice] = useState("");
    const [stok,setStok] = useState("");


    const handleVariantChange = () =>{
        setWithVariant(!withVariant)
    }



    const handleChangeOne = action => (e) =>{
        switch(action){
            case 0:
                setProductName(e.target.value);
                break;
            default:
                break;
        }
    }

    const goBack = () =>{
        history.push("/main/inventory")
    }

    return(
        <div className="addProductPage">
           <h3 className="title">Informasi Produk</h3>
           <div className="input-group">
                <label>Nama Produk</label>
                <input type="text" name="productName" value={productName} onChange={handleChangeOne(0)} placeholder="input"/>
           </div>

            {
            !withVariant ? 
                <WithoutVariant setPrice={setPrice} setStok={setStok} price={price} stok={stok} handleVariantChange={handleVariantChange} />
            : 
                <WithVariant handleVariantChange={handleVariantChange}  />
            }

            <div className="row_add_product">
                <button className="backButton" onClick={goBack}>Kembali</button>
                <button className="insertButton">Simpan</button>
            </div>
        
        </div>
    )   
}

export default AddProduct;
