import React, {useState, useEffect} from "react"
import AccountInput from "../AccountComponents/AccountInput";

const AddProductModal = (props) =>{
    const PRODUCTNAME = "productName";
    const DESCRIPTION = "description";
    const PRICE = "price";
    const STOCK = "stock";
    const [productName, setProductName] = useState("");
    const [description, setDescription] = useState("");
    const [price, setPrice] = useState("");
    const [stock, setStock] = useState("");

    const handleChange = (e) =>{
        switch(e.target.name){
            case PRODUCTNAME:
                setProductName(e.target.value); 
                break;
            case DESCRIPTION:
                setDescription(e.target.value);
                break;
            case PRICE:
                setPrice(e.target.value || 0);
                break;
            case STOCK:
                setStock(e.target.value || 0);
                break;
            default:
                break;
        }
        
    }

    return(
            <div className="overlay" style={props.overlayStyle}>
                <a onClick={props.closeModal} className="back">back</a>
                <div className="addProductModal">
                    <h3>Add product</h3>
                    <AccountInput 
                                label={"Name"} labelDesc={""} 
                                value={productName} handleChange={handleChange}
                                placeholder={"Excalibur"} name={PRODUCTNAME}/>
                    <AccountInput 
                                label={"Description"} labelDesc={""} 
                                value={description} handleChange={handleChange}
                                placeholder={"The Sword of king arthur"} name={DESCRIPTION}/>  
                    <AccountInput 
                                label={"Stock"} labelDesc={""} 
                                value={stock} handleChange={handleChange}
                                placeholder={"Excalibur"} name={PRODUCTNAME}/>
                    <AccountInput 
                                label={"Price"} labelDesc={""} 
                                value={price} handleChange={handleChange}
                                placeholder={"The Sword of king arthur"} name={DESCRIPTION}/>                                                                  
                </div>
            </div>
    );
}

export default AddProductModal;
