import React, {useState, useEffect} from "react"
import { useHistory } from "react-router"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrashAlt } from "@fortawesome/free-regular-svg-icons"
import { faArrowsAlt} from "@fortawesome/free-solid-svg-icons"

const AddProduct = (props) =>{

    const MAX_VARIANT = 20;
    const [dragidx,setdragIdx] = useState(0);
    const [withVariant,setWithVariant] = useState(false);
    const [variantList,setVariantList] = useState([{text:"",style:{}}]);

    const handleVariantChange = () =>{
        setWithVariant(!withVariant)
    }

    const addVariantList = () =>{
        if(variantList.length <= MAX_VARIANT){
            let newVariantList = [...variantList];
            newVariantList.push("");
            setVariantList(newVariantList);
        }

    }

    const handleInputChange = idx => (e) =>{
        let newVariant = [...variantList];
        newVariant[idx] = e.target.value;
        setVariantList(newVariant)
    }

    const deleteVariant = idx => (e) =>{
        let newVariantList = [...variantList];
        newVariantList.splice(idx,1);
        setVariantList(newVariantList)
    }

    const dragStart = idx => (e) =>{
        setdragIdx(parseInt(idx))
    }

    const dragEnter = idx => (e) =>{
        let newVariantList = [...variantList];
        if(idx > dragidx){
            newVariantList[idx].style = {transform:"translateY(100%)"};
            newVariantList[dragidx].style = {transform:"translateY(-100%)"};
        }else{
            newVariantList[idx].style={transform:"translateY(-100%"};
        }

        setTimeout(() =>{
            let newVariantList = [...variantList];
            const temp = newVariantList[idx];
            newVariantList[idx] = newVariantList[dragidx];
            newVariantList[dragidx] = temp;
            setVariantList(newVariantList);
            setdragIdx(idx);
        },300)

    }

    const dragOver = idx => (e) =>{
        e.preventDefault();
    }

    return(
        <div className="addProductPage">
           <h3 className="title">Informasi Produk</h3>
           <div className="input-group">
                <label>Nama Produk</label>
                <input type="text" name="productName" placeholder="input"/>
           </div>

            {!withVariant ? 
                <div>
                    <div className="input-group">
                        <label>Harga</label>
                        <input type="text" name="productName" placeholder="input"/>
                    </div>    
                    <div className="input-group">
                        <label>Stok</label>
                        <input type="text" name="productName" placeholder="input"/>
                    </div>
                    <div className="input-group">
                        <label>Variasi</label>
                        <button type="button" className="addVariasi" onClick={handleVariantChange}>+ Aktifkan Variasi</button>
                    </div>  
                </div> 
            : 
            <div>
                <div className="variant-container">
                    <label>Variasi 1</label>
                    <div className="variant-box">
                        <span className="close" onClick={handleVariantChange}>x</span>
                        <div className="input-group">
                            <label>Nama</label>
                            <input type="text" name="variantName" placeholder="Masukan nama variasi, contoh: warna,dll"/>
                        </div>
                        {variantList.map((x,i)=>{
                            return(
                                <div className="input-group" style={x.style} key={i} draggable={true} onDragStart={dragStart(i)} onDragEnter={dragEnter(i)}>
                                    <label >Pilihan</label>
                                    <input type="text" name="variantChoice" value={x.text} onChange={handleInputChange(i)} placeholder="Masukan pilihan variasi, contoh: Merah,dll"/>
                                    {(variantList.length > 1) ? 
                                        <div className="icon-wrapper">
                                            <FontAwesomeIcon icon={faTrashAlt} className="input-icon" onClick={deleteVariant(i)}/>
                                            <FontAwesomeIcon icon={faArrowsAlt} className="input-icon" id="draggableIcon" onDragOver={dragOver(i)} draggable={true}/>                                       
                                        </div>
                                     : ""}
                                </div>   
                            )                       
                        })}
                        <div className="input-group">
                            <label></label>
                            <button type="button" className="addVariasi" onClick={addVariantList}>+ Tambahkan Pilihan ({variantList.length + "/" + MAX_VARIANT})</button>
                        </div>  
                    </div>
                </div>                      
            </div>
            }
           
        
        </div>
    )   
}

export default AddProduct;
