import React, {useState, useEffect} from "react"
import {
  Switch,
  Route,
  Redirect
} from "react-router-dom";
import Home from "./components/Home"
import Main from "./components/Main"
import AuthenticationDataService from "./services/authentication"

function App() {  

  const [accountInfo,setAccountInfo] = useState(false);

  useEffect(()=>{
    getAuthenticated();
  },[])

  const getAuthenticated = () =>{
    AuthenticationDataService.getAuthenticatedUser()
        .then(res =>{
            setAccountInfo(res.data || true); 
        }) 
        .catch(e =>{
            console.log(e)
        }) 
  }   

  return (
    <Switch>
      <Route path={["/","/home"]}>
        {!accountInfo ? <div></div> : (typeof accountInfo === 'object' && accountInfo !== null) ? <Home accountInfo={accountInfo}/> : <Redirect to="/main"/>}

      </Route>
      <Route path="/main">
        <Main accountInfo={accountInfo}/>
      </Route>
    </Switch>
  );
}

export default App;
