import React, {useState, useEffect} from "react"
import "../../css/inventory.css"
import ProductsDataService from '../../services/products'
import ProductItems from './InventoryComponents/ProductItem'
import { useHistory } from "react-router"
import AddProductModal from "./InventoryComponents/AddProductModal";
import QueryBox from "./InventoryComponents/QueryBox";

const Inventory = (props) =>{
    const history = useHistory();
    const [query,setQuery] = useState("");

    const [ownedProducts,setOwnedProducts] = useState([]);
    const [overlayStyle,setOverlayStyle] = useState({});

    useEffect(()=>{
        refreshProductsList();
    },[])

    const refreshProductsList = () =>{
        ProductsDataService.getAllOwnedProducts().then((res)=>{
            setOwnedProducts(res.data);
        });
    }

    const typeQueryBox = (e) =>{
        setQuery(e.target.value)
    }    

    const addProductToggle = () =>{
        setOverlayStyle({left:0});
    }

    const closeModal = () =>{
        setOverlayStyle({left:"100%"})
    }

    return(
        <div id="main-inventory">
            <QueryBox />
            <div className="productsListContainer">
                <div className="inventory-row header">
                    <h4 className="productQtyText">0 Product</h4>
                    <button className="addProductButton"><span>+</span> Tambah Produk Baru</button>
                </div>
                <table class="table">
                    <thead class="thead">
                        <tr>
                            <th>Nama Produk</th>
                            <th>Variasi</th>
                            <th className="thSort">Harga</th>
                            <th>Stok</th>
                            <th>Penjualan</th>
                            <th>Status</th>
                        </tr>
                    </thead>
                </table>        
            </div>
        </div>
    )
}

export default Inventory;

/*
            <div className="queryBoxContainer">
                <input type="text" name="queryBox" id="queryBox" className="acc-text-input" value={query} onChange={typeQueryBox} placeholder="Search product here" /> 
                <button className="add-product" onClick={addProductToggle}>Add Product</button>
            </div>
            <div className="productListContainer">
                {ownedProducts.map(x=>{
                    return <ProductItems key={x._id} price={x.price} name={x.name} stock={x.stock}/>
                })}
            </div>

            <AddProductModal closeModal={closeModal} overlayStyle={overlayStyle} refreshProductsList={refreshProductsList}/>
