import React, {useState, useEffect} from "react"
import ProductsDataService from '../../../services/products'

const QueryBox = (props) =>{
    const [stokUpMax,setStokUpMax] = useState("0")
    const [sellUpMax,setSellUpMax] = useState("0")

    const handleChange = (e) =>{
        switch(e.target.id){
            case "queryType":
                setSelectedSearch(e.target.value);
                break;
            case "searchInput":
                setSearchInput(e.target.value);
                break;
            case "stokUp":
                setStokUpValue(e.target.value);
                break;
            case "stokDown":
                setStokDownValue(e.target.value);
                setStokUpMax(e.target.value);
                break;
            case "sellUp":
                setSellUpValue(e.target.value);
                break;
            case "sellDown":
                setSellDownValue(e.target.value);
                setSellUpMax(e.target.value)
                break;
            default:
                break;
        }
        
    }

    const reset = () => {
        setStokDownValue("");
        setStokUpValue("");
        setSellUpValue("");
        setSellDownValue("");
        setSearchInput("");
        setSelectedSearch("0");
        setStokUpMax("0");
        setSellUpMax("0");
        props.refreshProductsList();
    }

    const search = () =>{
        // by 0 is product name and by 1 is variation name
        const query = {
            input: searchInput,
            by: selectedSearch,
            stokUp: stokUpValue,
            stokDown: stokDownValue
        }

        ProductsDataService.find(query).then(res=>{
            props.setOwnedProducts(res.data)
        }).catch(e =>{
            console.log(e);
        })

    }

    return(
        <div className="queryBoxContainer mb-5">
            <div className="inventory-row" style={{marginBottom:"1rem"}}>
                <select name="searchBox" className="queryType" id="queryType" value={selectedSearch} onChange={handleChange}>
                    <option value="0">Nama Produk</option>
                    <option value="1">Variasi</option>
                </select>
                <input type="text" id="searchInput" name="searchInput" value={searchInput} onChange={handleChange} className="searchInput" placeholder="Input"/>
            </div>
            <div className="inventory-row" style={{marginBottom:"1rem"}}>
                <div className="rangeInputContainer" style={{paddingRight:"1rem"}}>
                    <label>Stok</label>
                    <input type="number" id="stokDown" name="stokDown" value={stokDownValue} min="0" onChange={handleChange} className="rangeInput" placeholder="input" />
                    <hr></hr>
                    <input type="number" id="stokUp" name="stokUp" value={stokUpValue} min={stokUpMax} onChange={handleChange} className="rangeInput" placeholder="input" />
                </div>
                <div className="rangeInputContainer" style={{paddingLeft:"1rem"}}>
                    <label>Penjualan</label>
                    <input type="number" id="sellDown" name="sellDown" value={sellDownValue} min="0" onChange={handleChange} className="rangeInput" placeholder="input" />
                    <hr></hr>
                    <input type="number" id="sellUp" name="sellUp" value={sellUpValue} min={sellUpMax} onChange={handleChange} className="rangeInput" placeholder="input" />
                </div>
            </div>
            <div className="inventory-row">
                <button className="searchButton" onClick={search}>Cari</button>
                <button className="resetButton" onClick={reset}>Atur Ulang</button>
            </div>
        </div>
    )
}

export default QueryBox;
