import React, {
    useState
} from "react"
import HomeInput from "../HomeInput"
import {UserIcon} from "../../../resources/Icons"

const EmailInput = props =>{

    //main attribute
    const [spanClass,setSpanClass] = useState("label");
    const spanText = "Username/email";
    let inputRightIcon = '';
    let inputType = 'text';
    let inputName = "username";
    let inputClass = (props.emailOK) ? "formInput borderBlack" : "formInput borderRed";

    const handleChange = event =>{
        props.setEmail(event.target.value);
        
        if(event.target.value === ""){
            setSpanClass("label")
        }else{
            setSpanClass("label spanUp")
        }
    }

    const Alert = () =>{      
        return(
            <p style={{visibility: (props.emailOK) ? "hidden" : "visible"}} className="inputAlert">Email is not valid</p>
        )
    }

    return(
        <HomeInput 
                spanClass={spanClass} setSpanClass={setSpanClass} spanText={spanText} 
                icon={<UserIcon />} inputRightIcon={inputRightIcon} 
                inputType={inputType} inputName={inputName}
                inputClass={inputClass} value={props.email} 
                handleChange={handleChange} alert={<Alert />} />
    )
}

export default EmailInput;
