import React, {useState, useEffect} from "react"
import "../../css/inventory.css"
import sampleImage from '../../images/products/Excalibur.png'
import tokopedia from '../../images/website/tokopedia-48.png'
import shopee from '../../images/website/shopee-48.png'
import lazada from '../../images/website/lazada-48.png'
import ProductsDataService from '../../services/products'
import ProductItems from './InventoryComponents/ProductItem'
import { useHistory } from "react-router"

const Inventory = (props) =>{
    const history = useHistory();
    const [query,setQuery] = useState("");
    const [ownedProducts,setOwnedProducts] = useState([]);
    const [fadeOut,setFadeOut] = useState("rgba(0,0,0,0)");

    useEffect(()=>{
        getOwnedProducts();
    },[])

    const getOwnedProducts = () =>{
        ProductsDataService.getAllOwnedProducts().then((res)=>{
            setOwnedProducts(res.data);
        });
    }

    const typeQueryBox = (e) =>{
        setQuery(e.target.value)
    }    

    const addProductToggle = () =>{
        setFadeOut("rgba(0,0,0,0.5)");
    }

    return(
        <div id="main-inventory">
            <div className="queryBoxContainer">
                <input type="text" name="queryBox" id="queryBox" className="acc-text-input" value={query} onChange={typeQueryBox} placeholder="Search product here" /> 
                <button className="add-product" onClick={addProductToggle}>Add Product</button>
            </div>
            <div className="productListContainer">
                {ownedProducts.map(x=>{
                    return <ProductItems key={x._id} id={x._id} name={x.name} stock={x.stock}/>
                })}
            </div>
            <div className="overlay" style={{backgroundColor:fadeOut}}>
                
            </div>
        </div>
    )
}

export default Inventory;
